#define TILE 0xd8
#define ATTR 0x01
#define ATTR2 0x61

#define NUM_BULLETS 1
#define BUL_LIFETIME 20
#define BUL_SPEED 10

#define NOTE_TABLE note_table
#define BASS_NOTE 36


unsigned char row, col, byte_col, bit;

typedef struct {
  int dir;
  int speed;
  int xpos;
  int ypos;
  bool in_use;
  int lifetime;
}Bullet;

Bullet bullets[NUM_BULLETS];

void init_bullet_list() {
  int i;
  for ( i = 0; i < NUM_BULLETS; i++) {
    bullets[i].in_use = false;
    bullets[i].lifetime = BUL_LIFETIME;
    bullets[i].speed = BUL_SPEED;
  }
}

void spawn_bullet(int px, int py, int dir) {
  	int i;
	for (i = 0; i < NUM_BULLETS; i++) {
        	if (!bullets[i].in_use) {
                	bullets[i].in_use = true;
                  	bullets[i].xpos = px;
                  	bullets[i].ypos = py;
                  	bullets[i].dir = dir;
                  	bullets[i].lifetime = BUL_LIFETIME;
                  	return;
                }
        }
  	return;
}

bool active_bullet() {
  	int i;
	for (i = 0; i < NUM_BULLETS; i++) {
        	if (bullets[i].in_use) {
                	return true;
                }
        }
  	return false;
}

void update_bullet(int i) {
  if (bullets[i].in_use) {
    bullets[i].xpos += bullets[i].dir * bullets[i].speed;
    bullets[i].lifetime -= 1;
    if (bullets[i].lifetime <= 0){
       bullets[i].in_use = false;
    }
  }
}

void reset_bullets() {
  int i;
  for (i = 0; i < NUM_BULLETS; i++) {
    bullets[i].in_use = false;
  }
}

