void fade_to_color() {
  pal_bright(0);
  ppu_wait_frame();
  ppu_wait_frame();
  ppu_wait_frame();
  pal_bright(1);
  ppu_wait_frame();
  ppu_wait_frame();
  ppu_wait_frame();
  pal_bright(2);
  ppu_wait_frame();
  ppu_wait_frame();
  ppu_wait_frame();
  pal_bright(3);
  ppu_wait_frame();
  ppu_wait_frame();
  ppu_wait_frame();
  pal_bright(4);
}



